function trim_path(string $path) {
    if (strpos($path, 'README.md') === false) {
        $path = str_replace('docs', '', $path);
        $path = str_replace('.md', '', $path);
    } else {
        $path = str_replace('docs', '', $path);
        $path = str_replace('/README.md', '', $path);
    }
    return $path;
}

