// Parse the markdown file
$files = scandir('docs/');
foreach ($files as $file) {
    $full = 'docs/' . $file;
    if (is_dir($full) and $full != 'docs/.gitbook') {
        $output_file_list[] = $full;
        unset($output_file_list[0]);
        unset($output_file_list[1]);
    }
}
//print_r($output_file_list);
foreach ($output_file_list as $key => $file) {
    $subfile = scandir($file);

    foreach ($subfile as $key => $subsubfile) {
        $output_subfile_list[] = $file . '/' . $subsubfile;
    }
}
foreach ($output_subfile_list as $key => $file) {
    $full = $file;
    $subfile = scandir($file);

    foreach ($subfile as $key2 => $subsubfile) {
        $output_subfile_list[] = $full . '/' . $subsubfile;
    }
}

foreach ($output_subfile_list as $key => $file) {
    if (substr($file, -3) === '.md') {
        // Get image URL
        $file_info = file_get_contents($file);
        preg_match_all('#\bhttps?://[^,\s()<>]+(?:\([\w\d]+\)|([^,[:punct:]\s]|/))#', $file_info, $image_urls);
        foreach ($image_urls[0] as $key2 => $legacy_url) {
            $url_key = array_search($legacy_url, $paths);
            if ($url_key) {
                $new_url = trim_path($file);
                $file_info = str_replace($legacy_url, $new_url, $file_info);
            }
            file_put_contents($file, $file_info);
        }
    }
}
